import React, {useContext, useState, useEffect} from 'react'
import {useParams} from 'react-router-dom'
import {GlobalState} from '../../../GlobalState'
import TankItem from '../utils/tanksItem/TankItem'


function DetailTank() {
    const params = useParams()
    const state = useContext(GlobalState)
    const [tank] = state.TanksAPI.tanks
    const [detailTank, setDetailTank] = useState([])

    useEffect(() =>{
        if(params.id){

            tank.forEach(tank => {
                if(tank._id === params.id) setDetailTank(tank)
            })
        }
    },[params.id, tank])

    if(detailTank.length === 0) return null;

    return (
        <>
            <div className="detail">
                <img src={detailTank.images.url} alt="" />
                <div className="box-detail">
                    <div className="row">
                        <h2>{detailTank.title}</h2>
                        <h6>#id: {detailTank.tank_id}</h6>
                    </div>
                    <td>site<p>{detailTank.side_id}</p></td>
                    <p>{detailTank.producer}</p>
                    <p>{detailTank.modyfication}</p>
                    <p>{detailTank.ammo}</p>
                    <p>{detailTank.armor}</p>
                    <p>{detailTank.milage}</p>
                    <p>{detailTank.datetank}</p>
                    <p>{detailTank.datetankc}</p>
                </div>
            </div>

            <div>
                <h2>Related tanks</h2>
                <div className="tanks">
                    {
                        tank.map(tank => {
                            return tank.category === detailTank.category 
                                ? <TankItem key={tank._id} tank={tank} /> : null
                        })
                    }
                </div>
            </div>
        </>
    )
}

export default DetailTank
