import styled from "styled-components";
import {mobile} from "../responsive";
import React, {useState} from 'react'
import {Link} from 'react-router-dom'
import axios from 'axios'

const Container = styled.div`
  width: 100vw;
  height: 100vh;
  background: linear-gradient(
      rgba(255, 255, 255, 0.5),
      rgba(255, 255, 255, 0.5)
    ),
    url("https://s1.1zoom.me/b5050/988/World_of_Tanks_Skoda_T_27_575194_1920x1080.jpg")
      center;
  background-size: cover;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Wrapper = styled.div`
  width: 25%;
  padding: 20px;
  background-color: white;
  ${mobile({ width: "75%" })}
`;

const Title = styled.h1`
  font-size: 24px;
  font-weight: 300;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
`;

const Input = styled.input`
  flex: 1;
  min-width: 40%;
  margin: 10px 0;
  padding: 10px;
`;

const Button = styled.button`
  width: 40%;
  border: none;
  padding: 15px 20px;
  background-color: teal;
  color: white;
  cursor: pointer;
  margin-bottom: 10px;
`;


const Login = () => {
  return (
    <Container>
      <Wrapper>
        <Title>Zaloguj</Title>
        <Form>
          <Input type="email" name="email" required
                 placeholder="E-mail" value={user.email} onChange={onChangeInput} />
          <Input type="password" name="password" required autoComplete="on"
                placeholder="Hasło" value={user.password} onChange={onChangeInput}  />
          <Button type="submit">Zaloguj</Button>
          <Link to="/register">Stwórz Nowe Konto</Link>
        </Form>
      </Wrapper>
    </Container>
  );
};

export default Login;
