import React, {useContext, useState, useEffect} from 'react'
import {useParams, Link} from 'react-router-dom'
import {GlobalState} from '../../../GlobalState'
import TankItem from '../utils/tanksItem/TankItem'


function DetailTank() {
    const params = useParams()
    const state = useContext(GlobalState)
    const [tank] = state.tanksAPI.tanks
    const addCart = state.userAPI.addCart
    const [detailTank, setDetailTank] = useState([])

    useEffect(() =>{
        if(params.id){

            tank.forEach(tank => {
                if(tank._id === params.id) setDetailTankt(tank)
            })
        }
    },[params.id, tanks])

    if(detailTank.length === 0) return null;

    return (
        <>
            <div className="detail">
                <img src={detailTank.images.url} alt="" />
                <div className="box-detail">
                    <div className="row">
                        <h2>{detailTank.title}</h2>
                        <h6>#id: {detailTank.tank_id}</h6>
                    </div>
                    <span>$ {detailTank.price}</span>
                    <p>{detailTank.description}</p>
                    <p>{detailTank.content}</p>
                    <p>Sold: {detailTank.sold}</p>
                    <Link to="/cart" className="cart"
                    onClick={() => addCart(detailTank)}>
                        Dodaj
                    </Link>
                </div>
            </div>

            <div>
                <h2>Related tanks</h2>
